        return new Source("{% extends 'base.html.twig' %}

{% block title %}Hello ProductController!{% endblock %}

{% block body %}
<style>
#customers {
  font-family: Arial, Helvetica, sans-serif;
  border-collapse: collapse;
  width: 100%;
}

#customers td, #customers th {
  border: 1px solid #ddd;
  padding: 8px;
}

#customers tr:nth-child(even){background-color: #f2f2f2;}

#customers tr:hover {background-color: #ddd;}

#customers th {
  padding-top: 12px;
  padding-bottom: 12px;
  text-align: left;
  background-color: #4CAF50;
  color: white;
}
</style>

<div class=\"example-wrapper\">

<form action=\"{{path('create_category')}}\">
    <button type=\"submit\" >Creaza Categorie Nou</button>
    </br>
    </br>
    </br>
</form>

<table id =\"customers\">
<th>ID</th>
<th>Nume</th>
<th>Actiune</th>
{% for category in categories %}
   <tr>
       <td> {{ category.id }} </td>

    <td> {{ category.name }} </td>
              <td>
            <div class=\"d-flex justify-content-around\">
           <a href=\"{{path('edit_category',{id:category.id})}}\"> <img style=\"width:20px;height:20px\" src = \"https://cdn0.iconfinder.com/data/icons/glyphpack/19/edit-512.png\"></img> </a>  
            <a class=\"text-danger\" href=\"{{path('delete_category',{id:category.id})}}\"><img style=\"width:20px;height:20px\" src = \"https://www.pngrepo.com/download/20658/delete.png\"></img></a>
            </div>
        </td>
    </tr>
{% else %}
    No users have been found.
{% endfor %}
</table>


 
</div>
{% endblock %}
